gadget.script:
opensocial.Container.setContainer(new opensocial.SampleContainer());
opensocial.Container.get().enableCaja();

// remove when have state parsing or server side impl
var container = opensocial.Container.get();
var viewer = container.newPerson({'id' : 1, 'name' : 'viewer guy'},
    false, true);
var owner = container.newPerson({'id' : 2, 'name' : 'owner guy'},
    true, false);
var viewerFriend1 = container.newPerson({'id' : 3,
    'name' : 'viewer friend 1!'});

container.resetData(viewer, owner,
    container.newCollection([viewerFriend1]), null, null, null, data);
